        // 回文字串对象，包括起始位置和长度
        class Item
        {
            public int start;
            public int count;
            public string Format(string s)
            {
                return s.Substring(start, count);
            }
        }
        /**
         * 5. 最长回文子串
         * 给你一个字符串 s，找到 s 中最长的回文子串。
         */
        public string LongestPalindrome(string s)
        {
            List<Item> list = new List<Item>();
            Item maxItem = new Item { start = 0, count = 0 };

            for (int i = 0; i < s.Length; ++i)
            {
                // 遍历当前所有的回文子串，看增加头尾之后是否仍然是回文串
                for (int j = list.Count - 1; j >= 0; --j)
                {
                    Item tm = list[j];
                    int newStart = tm.start - 1;
                    if (newStart >= 0 && s[newStart] == s[i])
                    {
                        tm.start = newStart;
                        tm.count += 2;
                        if (tm.count > maxItem.count)
                            maxItem = tm;
                    }
                    else
                    {
                        list.RemoveAt(j);
                    }
                }

                // 是否与前一个字母构成长度为2的回文串
                if (i > 0 && s[i] == s[i - 1])
                {
                    Item addItem2 = new Item { start = i - 1, count = 2 };
                    list.Add(addItem2);
                    if (addItem2.count > maxItem.count)
                        maxItem = addItem2;
                }

                // 自己本身是长度为1的回文串
                Item addItem = new Item { start = i, count = 1 };
                list.Add(addItem);
                if (addItem.count > maxItem.count)
                    maxItem = addItem;
            }

            return maxItem.Format(s);
        }

        /**
         * 647. 回文子串
         * 给定一个字符串，你的任务是计算这个字符串中有多少个回文子串
         * 具有不同开始位置或结束位置的子串，即使是由相同的字符组成，也会被视作不同的子串。
         */
        public int CountSubstrings(string s)
        {
            List<Item> list = new List<Item>();
            int count = 0;
            for (int i = 0; i < s.Length; ++i)
            {
                // 遍历当前所有的回文子串，看增加头尾之后是否仍然是回文串
                for (int j = list.Count - 1; j >= 0; --j)
                {
                    Item tm = list[j];
                    int newStart = tm.start - 1;
                    if (newStart >= 0 && s[newStart] == s[i])
                    {
                        tm.start = newStart;
                        tm.count += 2;
                    }
                    else
                    {
                        list.RemoveAt(j);
                    }
                }

                // 是否与前一个字母构成长度为2的回文串
                if (i > 0 && s[i] == s[i - 1])
                {
                    Item addItem2 = new Item { start = i - 1, count = 2 };
                    list.Add(addItem2);
                }

                // 自己本身是长度为1的回文串
                Item addItem = new Item { start = i, count = 1 };
                list.Add(addItem);

                count += list.Count;
            }
            return count;
        }

        /*
         * 132. 分割回文串 II
         * 给你一个字符串 s，请你将 s 分割成一些子串，使每个子串都是回文。
         * 返回符合要求的 最少分割次数 。
         * */
        public int MinCut(string s)
        {
            // dp[i]表示0到i的子串最少有多少个回文串构成
            // 但是注意题目返回是切割的次数，所以最终返回时要减1
            int[] dp = new int[s.Length];

            // 以下代码跟05的一样，维护当前构成回文的所有子串并动态扫描更新
            List<Item> list = new List<Item>();

            for (int i = 0; i < s.Length; ++i)
            {
                dp[i] = i + 1;

                for (int j = list.Count - 1; j >= 0; --j)
                {
                    Item tm = list[j];
                    int newStart = tm.start - 1;
                    if (newStart >= 0 && s[newStart] == s[i])
                    {
                        tm.start = newStart;
                        tm.count += 2;
                    }
                    else
                    {
                        list.RemoveAt(j);
                    }
                }

                if (i > 0 && s[i] == s[i - 1])
                {
                    Item addItem2 = new Item { start = i - 1, count = 2 };
                    list.Add(addItem2);
                }

                Item addItem = new Item { start = i, count = 1 };
                list.Add(addItem);

                // 只有这里加一个动态规划递推，当(start,i)为一个回文串时，当前最小字串个数为
                // 这一个子串加前面的最小个数，即dp[start - 1] + 1
                foreach (var tm in list)
                {
                    int count = (tm.start > 0 ? dp[tm.start - 1] : 0) + 1;
                    if (dp[i] > count) dp[i] = count;
                }
            }

            return dp[s.Length - 1] - 1;
        }

        // 131. 分割回文串
        public IList<IList<string>> Partition(string s)
        {
            IList<IList<string>>[] arr = new IList<IList<string>>[s.Length];

            // 以下代码跟05的一样，维护当前构成回文的所有子串并动态扫描更新
            List<Item> list = new List<Item>();

            for (int i = 0; i < s.Length; ++i)
            {
                for (int j = list.Count - 1; j >= 0; --j)
                {
                    Item tm = list[j];
                    int newStart = tm.start - 1;
                    if (newStart >= 0 && s[newStart] == s[i])
                    {
                        tm.start = newStart;
                        tm.count += 2;
                    }
                    else
                    {
                        list.RemoveAt(j);
                    }
                }

                if (i > 0 && s[i] == s[i - 1])
                {
                    Item addItem2 = new Item { start = i - 1, count = 2 };
                    list.Add(addItem2);
                }

                Item addItem = new Item { start = i, count = 1 };
                list.Add(addItem);

                arr[i] = new List<IList<string>>();
                foreach (var tm in list)
                {
                    if (tm.start > 0)
                    {
                        foreach (var pl in arr[tm.start - 1])
                        {
                            IList<string> nl = new List<string>(pl);
                            nl.Add(tm.Format(s));
                            arr[i].Add(nl);
                        }
                    }
                    else
                    {
                        arr[i].Add(new List<string>() { tm.Format(s) });
                    }
                }
            }

            return arr[s.Length - 1];
        }

