        public bool CheckPartitioning(string s)
        {
            int[] dp = new int[s.Length];

            List<Item> list = new List<Item>();

            for (int i = 0; i < s.Length; ++i)
            {
                dp[i] = 0;

                for (int j = list.Count - 1; j >= 0; --j)
                {
                    Item tm = list[j];
                    int newStart = tm.start - 1;
                    if (newStart >= 0 && s[newStart] == s[i])
                    {
                        tm.start = newStart;
                        tm.count += 2;
                    }
                    else
                    {
                        list.RemoveAt(j);
                    }
                }

                if (i > 0 && s[i] == s[i - 1])
                {
                    Item addItem2 = new Item { start = i - 1, count = 2 };
                    list.Add(addItem2);
                }

                Item addItem = new Item { start = i, count = 1 };
                list.Add(addItem);

                foreach (var tm in list)
                {
                    if (tm.start > 0)
                    {
                        int map = dp[tm.start - 1];
                        if ((map & 2) != 0) dp[i] |= 4; // b2
                        if ((map & 4) != 0) dp[i] |= 8; // b3
                    }
                    else
                    {
                        dp[i] |= 2; // b1
                    }
                }
            }

            return (dp[s.Length - 1] & 8) != 0;
        }

        // 516. 最长回文子序列
        public int LongestPalindromeSubseq(string s)
        {
            int[,] dp = new int[s.Length, s.Length];
            for (int len = 1; len <= s.Length; ++len)
            {
                int end;
                for (int start = 0; (end = start + len - 1) < s.Length; ++start)
                {
                    if (len == 1)
                        dp[start, end] = 1;
                    else if (len == 2)
                        dp[start, end] = s[start] == s[end] ? 2 : 1;
                    else
                        dp[start, end] = Math.Max(
                            (s[start] == s[end] ? 2 : 0) + dp[start + 1, end - 1],
                            Math.Max(dp[start + 1, end], dp[start, end - 1])
                        );
                }
            }
            return dp[0, s.Length - 1];
        }

