            //        sb2.Append(s[s.Length - i - 1]);
            //    sb2.Append(s);
            //    return sb2.ToString();
            //}

            for (int i = 0; i < s.Length; ++i)
            {
                // 遍历当前所有的回文子串，看增加头尾之后是否仍然是回文串
                for (int j = list.Count - 1; j >= 0; --j)
                {
                    Item tm = list[j];
                    int newStart = tm.start - 1;
                    if (newStart >= 0 && s[newStart] == s[i])
                    {
                        tm.start = newStart;
                        tm.count += 2;
                        if (tm.start == 0 && tm.count > maxItem.count)
                            maxItem = tm;
                    }
                    else
                    {
                        list[j] = list[list.Count - 1];
                        list.RemoveAt(list.Count - 1);
                    }
                }

                // 是否与前一个字母构成长度为2的回文串
                if (i > 0 && s[i] == s[i - 1])
                {
                    Item addItem2 = new Item { start = i - 1, count = 2 };
                    list.Add(addItem2);
                    if (addItem2.start == 0 && addItem2.count > maxItem.count)
                        maxItem = addItem2;
                }

                // 自己本身是长度为1的回文串
                Item addItem = new Item { start = i, count = 1 };
                list.Add(addItem);
                if (addItem.start == 0 && addItem.count > maxItem.count)
                    maxItem = addItem;
            }

            int addCount = s.Length - maxItem.count;
            StringBuilder sb = new();
            for (int i = 0; i < addCount; ++i)
                sb.Append(s[s.Length - i - 1]);
            sb.Append(s);
            return sb.ToString();
        }

        // #214 ver2: 使用KMP
        // 问题转化为s与reversed_s的最长公共子序列(长度为count)
        public string ShortestPalindrome_KMP(string s)
        {
            int[] next = new int[s.Length]; // next[i]表示最长后缀
            Array.Fill(next, -1);
            for (int i = 1; i < next.Length; ++i)
            {
                if (next[i - 1] >= 0 && s[next[i - 1] + 1] == s[i])
                    next[i] = next[i - 1] + 1;
                else if (s[i] == s[0])
                    next[i] = 0;
            }
            int count = 0;
            for (int k = s.Length - 1; k >= 0; --k)
            {
                char c = s[k];
                while (true)
                {
                    if (s[count] == c)
                    {
                        count++;
                        break;
                    }
                    else if (count > 0)
                    {
                        count = next[count - 1] + 1;
                    }
                    else break;
                }
            }

            int addCount = s.Length - count;
            StringBuilder sb = new();
            for (int i = 0; i < addCount; ++i)
                sb.Append(s[s.Length - i - 1]);
            sb.Append(s);
            return sb.ToString();
        }
