    class P0085最大矩形
    {
        // 第二版解法：使用P0084柱状图中最大的矩形 中的算法
        public int MaximalRectangle(char[][] matrix)
        {
            if (matrix.Length == 0) return 0;
            int[] heights = new int[matrix[0].Length];
            int maxArea = 0;
            foreach (var mr in matrix)
            {
                List<int[]> list = new List<int[]>();
                for (int hi = 0; hi < heights.Length; ++hi)
                {
                    int h = heights[hi] = mr[hi] == '0' ? 0 : heights[hi] + 1;
                    int i, area;
                    for (i = 0; i < list.Count; ++i)
                    {
                        if (list[i][0] > h) break;
                        area = list[i][1] += list[i][0];
                        if (area > maxArea) maxArea = area;
                    }
                    if (i < list.Count) list.RemoveRange(i, list.Count - i);
                    if (h > 0 && (i == 0 || h > list[i - 1][0]))
                    {
                        area = h;
                        for (i = hi - 1; i >= 0 && heights[i] >= h; --i) area += h;
                        list.Add(new int[] { h, area });
                        if (area > maxArea) maxArea = area;
                    }
                }
            }
            return maxArea;
        }


        int maxArea, m, n;
        char[][] mat;
        HashSet<string> hash;

        void StartScan(int i, int j, int h, int w, int r, int c)
        {
            if (h <= 0 || w <= 0 || h * w <= maxArea) return;

            string key = $"{i},{j},{h},{w}";
            if (!hash.Contains(key))
            {
                hash.Add(key);
                Scan(i, j, h, w, r, c);
            }
        }

        void EndScan(int h, int w)
        {
            int area = h * w;
            if (area > maxArea) maxArea = area;
        }

        void Scan(int i, int j, int h, int w, int r, int c)
        {
            for (; r < i + h; ++r)
            {
                while (c < j + w)
                {
                    if (mat[r][c] == '0')
                    {
                        // 上方区域：一直扫描都没遇到0，可以直接结算面积
                        EndScan(r - i, w);

                        // 下方区域
                        StartScan(r + 1, j, h - r - 1 + i, w, r + 1, j);

                        // 左边区域
                        StartScan(i, j, h, c - j, r + 1, j);

                        // 右边区域
                        StartScan(i, c + 1, h, w - c - 1 + j, r, c + 1);

                        return;
                    }

                    ++c;
                }
                c = j;
            }
            EndScan(h, w);
        }

        // 第一版：在美版网站以5%过了，在国内网站超时。。
        public int MaximalRectangle1(char[][] matrix)
        {
            maxArea = 0;
            mat = matrix; m = matrix.Length; n = m == 0 ? 0 : matrix[0].Length;
            hash = new HashSet<string>();
            Scan(0, 0, m, n, 0, 0);
            return maxArea;
        }

