#if BACKUP 
       void Scan(int i, int j, int h, int w, int r, int c)
       {
            if (r >= i + h)
            {
                EndScan(h, w);
            }
            else if (c >= j + w)
            {
                Scan(i, j, h, w, r + 1, j);
            }
            else if (mat[r][c] == '1')
            {
                Scan(i, j, h, w, r, c + 1);
            }
            else // mat[r][c] == '0'
            {
                // 上方区域：一直扫描都没遇到0，可以直接结算面积
                EndScan(r - i, w);

                // 下方区域
                StartScan(r + 1, j, h - r - 1 + i, w, r + 1, j);

                // 左边区域
                StartScan(i, j, h, c - j, r + 1, j);

                // 右边区域
                StartScan(i, c + 1, h, w - c - 1 + j, r, c + 1);
            }
        }
#endif
