    internal class P0934最短的桥
    {
        public int ShortestBridge(int[][] grid)
        {
            int i = 0, j = 0;
            for (i = 0; i < grid.Length; ++i)
            {
                //Console.WriteLine("i=" + i);
                bool found = false;
                for (j = 0; j < grid[i].Length; ++j)
                    if (found = grid[i][j] == 1) break;
                //Console.WriteLine("j=" + j);

                if (found) break;
                //if (grid[i][j] == 1) break;
            }
            // Console.WriteLine("{0} {1} -> 1", i, j);
            // return -1;

            Queue<(int, int)> q1 = new();
            q1.Enqueue((i, j));
            grid[i][j] = 2;
            Queue<(int, int, int)> q2 = new();

            while (q1.Any())
            {
                (i, j) = q1.Dequeue();
                //Console.WriteLine("{0} {1} => 2", i, j);
                foreach ((int x, int y) in grid.FourDir(i, j))
                    if (grid[x][y] == 1)
                    {
                        grid[x][y] = 2; // 要在这里就mark，避免重复搜索
                        q1.Enqueue((x, y));
                    }
                    else if (grid[x][y] == 0)
                    {
                        grid[x][y] = 3;
                        q2.Enqueue((x, y, 1));
                    }
            }
            //return -1;

            while (q2.Any())
            {
                (int x, int y, int p) = q2.Dequeue();
                //grid[x][y] = 3;
                //Console.WriteLine("{0} {1} -> {2}", x, y, p);
                //if (p == 1) return p;
                foreach ((int a, int b) in grid.FourDir(x, y))
                    if (grid[a][b] == 1)
                        return p;
                    else if (grid[a][b] == 0)
                    {
                        grid[a][b] = 3;
                        q2.Enqueue((a, b, p + 1));
                    }
            }

            return -1;
        }

